// src/components/Gameboard.js

import React from 'react';
import PlayerHand from './PlayerHand';
import OpponentHand from './OpponentHand';
import DrawPile from './DrawPile';
import DiscardPile from './DiscardPile';
import ActionPanel from './ActionPanel';

const GameBoard = () => {
  // Include any state or handlers you need for the gameboard and pass them to the components

  return (
    <div className="game-board">
      <OpponentHand />
      <DrawPile />
      <DiscardPile />
      <PlayerHand onClick={handleCardClick} /> // Pass the click handler to PlayerHand
      <ActionPanel />
    </div>
  );
}

const handleCardClick = (card) => {
  console.log("Clicked card:", card);
  // Here you would include logic to handle clicking a card
};

export default GameBoard;
