// src/components/Card.js

import React from 'react';
import '../Card.css'; // Make sure the path is correct based on your project structure

const Card = ({ color, value, onClick }) => {
  return (
    <div className={`card ${color}`} onClick={onClick}>
      <span className="card-value">{value}</span> {/* Added span with class card-value */}
    </div>
  );
}

export default Card;
