import React, { useState } from 'react';
import PlayerHand from './PlayerHand';

// For generating and dealing the initial cards
const generateInitialDrawPile = () => {
  const colors = ['red', 'yellow', 'green', 'blue'];
  const values = Array.from({length: 9}, (_, i) => i + 1); // 1-9 for each color
  let deck = [];

  colors.forEach(color => {
    values.forEach(value => {
      deck.push({ id: `${color}-${value}`, color: color, value: value.toString() });
    });
  });

  // Shuffle deck
  for (let i = deck.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [deck[i], deck[j]] = [deck[j], deck[i]]; // Swap
  }

  return deck;
};


const dealInitialHands = (drawPile) => {
  const hands = { 0: [], 1: [] };
  const handSize = 7; // Each player starts with 7 cards

  for (let i = 0; i < handSize; i++) {
    for (let playerId in hands) {
      let card = drawPile.pop();
      hands[playerId].push(card);
    }
  }

  return hands;
};


const GameBoard = () => {
  const [drawPile, setDrawPile] = useState(generateInitialDrawPile());
  const [hands, setHands] = useState(dealInitialHands(drawPile));
  const [discardPile, setDiscardPile] = useState([]);
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);
  // Add more state variables as needed, such as current color, direction of play, etc.

  const handleCardClick = (card) => {
    if (isValidPlay(card, discardPile)) {
      playCard(card);
      applyCardEffect(card);
      advanceTurn();
    } else {
      alert("Invalid move!");
    }
  };

  const isValidPlay = (card, discardPile) => {
    const topCard = discardPile[discardPile.length - 1];
    return card.color === topCard.color || card.value === topCard.value;
  };


  const playCard = (card, currentPlayerIndex, hands, discardPile, setHands, setDiscardPile) => {
    const newHand = hands[currentPlayerIndex].filter(c => c.id !== card.id);
    const newHands = { ...hands, [currentPlayerIndex]: newHand };
    const newDiscardPile = [...discardPile, card];

    setHands(newHands);
    setDiscardPile(newDiscardPile);
  };


  const applyCardEffect = (card) => {
    switch (card.value) {
      case 'Skip':
        advanceTurn(); // Skip the next player
        break;
      case 'Reverse':
        reversePlayDirection();
        break;
      case 'Draw Two':
        drawCards(nextPlayer, 2);
        advanceTurn();
        break;
      case 'Wild':
        // Allow the player to choose a color
        break;
      case 'Wild Draw Four':
        // Allow the player to choose a color and the next player draws four cards
        drawCards(nextPlayer, 4);
        advanceTurn();
        break;
      default:
        // No special effect, just advance the turn
        break;
    }
  };


  const advanceTurn = (currentPlayerIndex, setCurrentPlayerIndex, playerCount) => {
    setCurrentPlayerIndex((currentPlayerIndex + 1) % playerCount);
  };


  return (
    <div className="game-board">
      {/* Pass the current player's hand and the click handler */}
      <PlayerHand hand={hands[currentPlayerIndex]} onClick={handleCardClick} />
      {/* Other components will go here */}
    </div>
  );
};

export default GameBoard;

