// src/App.js

import React, {useState} from 'react';
import GameBoard from './components/GameBoard'; // Assuming the file is named 'GameBoard.js'
import './App.css';

const App = () => {
  const [gameState, setGameState] = useState({
    hands: {}, // player id mapped to their hand
    drawPile: [],
    discardPile: [],
    currentPlayerIndex: 0,
    playDirection: 1, // 1 for forward, -1 for reverse
    currentColor: null, // important for wild cards
    unoCalled: false,
  });

  return (
    <div className="app">
      <GameBoard gameState={gameState} onCardPlayed={handleCardPlayed} onDrawCard={handleDrawCard} />

    </div>
  );
}

export default App;
