// src/components/PlayerHand.js

import React from 'react';
import '../Card.css'; // Make sure to create a Card.css file for styling
import Card from './Card';
const PlayerHand = ({ hand, onCardClick }) => {
  // Ensure `hand` and `onCardClick` are passed as props from the parent component

  return (
    <div className="player-hand">
      {hand.map(card => (
        <Card
          key={card.id} // Unique identifier for React's rendering
          color={card.color} // Passes the card's color for styling
          value={card.value} // Passes the card's value for display
          onClick={() => onCardClick(card)} // Handles click events on individual cards
        />
      ))}
    </div>
  );
};

export default PlayerHand;
