// src/components/PlayerHand.js

import React from 'react';
import '../Card.css'; // Make sure to create a Card.css file for styling
import Card from './Card';

// Mock data for the cards, in a real app this would come from state or props
const mockHand = [
  { id: 'card-1', color: 'red', value: '5' },
  { id: 'card-2', color: 'yellow', value: '1' },
  { id: 'card-3', color: 'blue', value: 'Skip' },
  { id: 'card-4', color: 'green', value: 'Reverse' },
  { id: 'card-5', color: 'red', value: 'Draw Two' },
  // Add more cards as needed
];


const PlayerHand = ({ hand, onCardClick }) => {
  return (
    <div className="player-hand">
      {hand.map((card) => (
        <Card
          key={card.id}
          color={card.color}
          value={card.value}
          onClick={() => onCardClick(card)}
        />
      ))}
    </div>
  );
};


export default PlayerHand;
